import { useState, useContext } from 'react';
import { Link } from 'react-router-dom';
import { AuthContext } from '../../AuthProvider';

export const Dropdown = () => {
  const { currentUser } = useContext(AuthContext);
  const [state, setState] = useState({ isOpen: false });
  const handleClick = () => {
    state.isOpen ? setState({ isOpen: false }) : setState({ isOpen: true })
  }

  return (
    <div>
      <button onClick={handleClick} className='hover:bg-black text-color p-3 w-32 relative'>
        {currentUser.name}
      </button>
      {state.isOpen ? 
        <div className='bg-white border absolute'>
          <Link to="/dashboard"><div>ダッシュボード</div></Link>
          <Link to="#"><div>アカウント設定</div></Link>
          <Link to="#"><div>ログアウト</div></Link>
        </div>
      :
        null }
    </div>
  );
}
