import { useState } from "react";
import { motion, AnimatePresence } from 'framer-motion'
const array = [{ id: 1, title: "リンク" }, { id: 2, title: "<a>" }, { id: 3, title: "段落" }, { id: 4, title: "<p>" }]

export const Test = () => {
  const [[word_id,direction], setWordId] = useState([0,1]);
  const handleOnClick = (newDirection) => {
    setWordId([word_id + newDirection, newDirection]);
  }

  return (
    <AnimatePresence initial={false}>
      <motion.div key={array[word_id].id} exit={{ x: direction > 0 ? -1000 : 1000 }} transition={{ type: "tween", duration: 0.5 }}>
        <div className="h-96 w-48 absolute left-48" onClick={handleOnClick(1)}></div>
        <div className="h-96 w-48 absolute" onClick={handleOnClick(-1)}></div>
        <div className="flex justify-center items-center text-3xl w-96 bg-red-500 h-96 border absolute -z-10">
          {array[0].title}
        </div>
      </motion.div>
    </AnimatePresence>
  );
}
