import { useContext } from 'react';
import { AuthContext } from '../../AuthProvider';
import DashboardHeader from "../groups/DashboardHeader";

export default function Dashboard() {
  const { currentUser, cards } = useContext(AuthContext);

  return (
    <div>
      <DashboardHeader />
      <div className="flex flex-col items-center top-color h-96">
        <div className='flex justify-center items-center h-20'>{currentUser.name}</div>
        <div>
          <ul className='grid-cols-4'>
            {cards.map((card) =>
              <li key={card.id} className='flex flex-col items-center w-60 p-6 m-4 rounded-md border bg-white'>
                <p>{card.title}</p>
                <p>{card.text}</p>
              </li>
            )}
          </ul>
        </div>
      </div>
    </div>
  );
}
