import { useContext, useEffect, useState } from 'react';
import { AuthContext } from '../../AuthProvider';
import { Link, useParams } from 'react-router-dom';
import DashboardHeader from "../groups/DashboardHeader";
import { getWords } from '../../apis/getWords';
import LearningButton from '../buttons/LearningButton';

export const Words = () => {
  const { id } = useParams();
  const { setLoading, cards } = useContext(AuthContext);
  let [words, setWords] = useState([]);
  const card = cards[id - 1].title;
  let new_words = [];

  const handleGetWords = async () => {
    try {
      const res = await getWords(id);
      if (res?.data.status === 200) {
        // setWords(res?.data.words);
        words = res?.data.words;
        const words_q = words.map(word_q =>{
          let j = 1;
          j =+ 2;
          return { id: j, word_main: word_q.question}
        });
        const words_a = words.map(word_a =>{return {word_main: word_a.answer, word_t: word_a.text}});
        const length = words_q.length*2-1;
        for (let i = 0; i<=length; i++){
          if (i%2===0) {
            new_words.push(words_q[i/2]);
          } else {
            new_words.push(words_a[(i-1)/2]);
          }
        }
        setWords(new_words);
      } else {
        console.log('no words');
      }
    } catch (err) {
      console.log(err);
    }
    setLoading(false);
  };

  useEffect(() => {
    handleGetWords();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div>
      <DashboardHeader />
      <div className="flex flex-col items-center top-color h-96">
        <div className='mt-6 text-color'>
          カード名: {card}<br />
          id: {id}
        </div>
        <div className='m-6'>
          <Link to="/learning" state={{card: card, id: id, words: words}} ><LearningButton text="暗記する" /></Link>
        </div>
        <div className='flex flex-col'>
          {words.map((word) =>
            <div key={word.word_main} className='flex flex-col items-center w-60 p-6 m-2 rounded-md border bg-white'>
              <div className="">{word.word_main}</div>
              <div className='text-sm'>{word.word_t}</div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
