export const Word = (props) => {
  return (
    <div>
      <div className="flex flex-col items-center top-color h-96">
        <div className='flex flex-wrap'>
          <div className='flex flex-col items-center w-60 p-6 m-2 rounded-md border hover:bg-gray-100 bg-white'>
            <div className='text-sm'>{props.words.question}</div>
            <div className='mt-3 text-sm'>{props.word.answer}</div>
            <div className='mt-3 text-sm'>{props.word.text}</div>
          </div>
        </div>
      </div>
    </div>
  );
}
