import { useEffect, useContext } from 'react';
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import { AuthContext } from './AuthProvider';
import Top from './components/pages/Top';
import Dashboard from './components/pages/Dashboard';
import { getCurrentUser } from './apis/login';

export default function Router() {
  const { loading, setLoading, isSignedIn, setIsSignedIn, currentUser, setCurrentUser } = useContext(AuthContext);

  const handleGetCurrentUser = async () => {
    try {
      const res = await getCurrentUser();//login中のuserがいるか確認する
      if (res?.data.logged_in === true) {
        setIsSignedIn(true);//ログインuserがいたらtrue
        setCurrentUser(res?.data.user);//ログインuserをセット
      } else {
        console.log('no current user');
      }
    } catch (err) {
      console.log(err);
    }
    setLoading(false);
  };

  //ログインユーザーがいるか確認
  useEffect(() =>{
    handleGetCurrentUser();
  }, [setCurrentUser])

  const PrivateRoute = ( {children} ) => {
    if (!loading) {
      if (isSignedIn) {
        return children ;
      } else {
        return <Navigate to='/' />;
      }
    } else {
      return <></>;
    }
  };

  const TopRoute = ( {children} ) => {
    if (!loading) {
      if (isSignedIn) {
        return <Navigate to='/dashboard'/> ;
      } else {
        return children;
      }
    } else {
      return <></>;
    }
  };

  return (
    <BrowserRouter>
      <Routes>
        <Route index element={<TopRoute><Top /></TopRoute>} />
        <Route path='/dashboard' element={<PrivateRoute><Dashboard /></PrivateRoute>} />
      </Routes>
    </BrowserRouter>
  );
}

// '/'にアクセスがあった場合
// ブラウザのsession_idがredisにもあれば、'/dashboard'にリダイレクト

