import { useState, useEffect, useContext } from "react";
import { AuthContext } from '../../AuthProvider';
import { Header } from "../groups/Header";
import Modal from "../modals/Modal";
import SignupForm from "../forms/SignupForm";
import TopSignupButton from "../buttons/TopSignupButton";
import '../../index.css';
import { fetchAllWords } from '../../apis/words';

export default function Top() {
  const initialState = { signupModalIsOpen: false };
  const [state, setState] = useState(initialState);
  const [word, setWord] = useState();
  const { setLoading } = useContext(AuthContext);

  function getRandomInt(min,max) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min) + min);
  }

  const handleFetchAllWords = async() => {
    try {
      const res = await fetchAllWords();
      if (res?.status === 200) {
        const random_word = res?.words[getRandomInt(0,res?.words.length)];
        setWord(random_word);
      } else {
        console.log('no current user');
      }
    } catch (err) {
      console.log(err);
    }
    setLoading(false);
  }

  const handleClickSignup = () => {
    state.signupModalIsOpen ? setState({ signupModalIsOpen: false }) : setState({ signupModalIsOpen: true });
  };

  useEffect(() => {
    handleFetchAllWords();
  }, []);

  return (
    <div>
      <Header />
      <div className="flex justify-center items-center h-96 top-color">
        <div className="flex flex-col items-center">
          <h2 className="text-center leading-normal text-2xl font-bold text-color">今日の霞が関用語</h2>
          {word? 
          <div className="flex flex-col items-center border">
            <div>{word.kana}</div>
            <div className="text-3xl">{word.question}</div>
            <div>{word.answer}<></>{word.text}</div>
          </div>
            : null }
          <div onClick={handleClickSignup} className='mt-14'>
            <TopSignupButton text="はじめる" />
          </div>
        </div>
      </div>
      <div className="flex justify-center items-center h-72 top-color-yellow">
      </div>
      {state.signupModalIsOpen ? <Modal child={<SignupForm />} onClick={handleClickSignup} /> : null}
    </div>
  );
}
