import { useContext, useEffect } from 'react';
import { AuthContext } from '../../AuthProvider';
import { getCards } from '../../apis/dashboard';

export default function Dashboard() {
  const { setLoading, currentUser } = useContext(AuthContext);
  // const { cards, setCards } = useState([]);
  let cards = [];
  // const addCards = (res) => {
  //   const data = res?.data.cards
  //   console.log(data);
  //   setCards([...cards, {text: data.text, title: data.title}]);
  // }

  const handleGetCards = async () => {
    try {
      const res = await getCards(currentUser);
      if (res?.data.status === 200) {
        console.log(res?.data.cards[0])
        cards = res?.data.cards;
        console.log(cards);
      } else {
        console.log('no cards');
      }
    } catch (err) {
      console.log(err);
    }
    setLoading(false);
  };

  useEffect(() => {
    handleGetCards();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div>
      <h1>Dashboard</h1>
      <p>{currentUser.name}さんのダッシュボード</p>
      <p>{!!cards}</p>
      {cards.map((card) => { return <p>{card.title}</p> }) }
    </div>
  );
}
