import { useState } from 'react';
import { useLocation } from 'react-router-dom';
import DashboardHeader from "../groups/DashboardHeader";
import { Word } from '../groups/Word';

export const Learning = () => {
  const location = useLocation();
  const { card, id, words } = location.state;
  const [count, setCount] = useState(1);
  const [word_id, setWordId] = useState(1);
  const [word, setWord] = useState(words[word_id].question);

  const handleOnClick = () => {
    setCount(count + 1);
    console.log(word_id)
    if (count % 2 === 0) {
      setWordId(count - count / 2);
      setWord(words[word_id].answer);
      console.log(word_id)
    } else {
      setWordId(count - (count - 1) / 2);
      setWord(words[word_id].question);
      console.log(count)
    }
  }

  return (
    <div>
      <DashboardHeader />
      <div className="flex flex-col items-center top-color h-96">
        <div className='mt-6 text-color'>
          カード名: {card}<br />
          カードid: {id}
        </div>
        <div className='relative w-full'>
          <Word word={word} onClick={handleOnClick} />
        </div>
      </div>
    </div>
  );
}
