import { useContext, useEffect, useReducer } from 'react';
import { AuthContext } from '../../AuthProvider';
import { initialState, wordsActionTypes, wordsReducer } from '../../reducers/words'
import { Link, useParams } from 'react-router-dom';
import { fetchWords } from '../../apis/words';
import DashboardHeader from "../groups/DashboardHeader";
import LearningButton from '../buttons/LearningButton';

export const Words = () => {
  const { cards } = useContext(AuthContext);
  const [state, dispatch] = useReducer(wordsReducer, initialState);
  const { id } = useParams();
  const card = cards[id - 1].title;
        // const question_words = words.map(question_word => { return { title: question_word.question } });
        // const answer_words = words.map(answer_word => { return { title: answer_word.answer, text: answer_word.text } });
        // const length = question_words.length * 2 - 1;
        // for (let i = 0; i <= length; i++) {
        //   if (i % 2 === 0) {
        //     new_words.push({id: i, title: answer_words[i / 2].title});
        //   } else {
        //     new_words.push({id: i, title: words_a[(i - 1) / 2]);
        //   }
        // }

  useEffect(() => {
    dispatch({type: wordsActionTypes.FETCHING});
    fetchWords(id)
      .then((data) => dispatch({
        type: wordsActionTypes.FETCH_SUCCESS,
        payload: {
          words: data.words
        }
      }))
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div>
      <DashboardHeader />
      <div className="flex flex-col items-center top-color h-96">
        <div className='mt-6 text-color'>
          カード名: {card}<br />
          id: {id}
        </div>
        <div className='m-6'>
          <Link to="/learning"><LearningButton text="暗記する" /></Link>
        </div>
        <div className='flex flex-col'>
          {state.wordsList.map((word) =>
            <div key={word.id}>{word.question}
              {/* <div key={word.word_main} className='flex flex-col items-center w-60 p-6 m-2 rounded-md border bg-white'>
              <div className="">{word.word_main}</div>
              <div className='text-sm'>{word.word_t}</div> */}
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
