import { useState } from 'react';
import { useLocation } from 'react-router-dom';
import DashboardHeader from "../groups/DashboardHeader";

export const Learning = () => {
  const location = useLocation();
  const { card, id, words } = location.state;
  const [count, setCount] = useState(1);
  const handleOnClick =()=>{
    setCount(count+1);
  }

  return (
    <div>
      <DashboardHeader />
      <div className="flex flex-col items-center top-color h-96">
        <div className='mt-6 text-color'>
          カード名: {card}<br />
          カードid: {id}
        </div>
        <div className='relative w-full'>
          {words.map((word) =>
            <div key={word.id} className={`flex justify-center items-center w-full h-64 border bg-white absolute`} onClick={handleOnClick}>

            </div>
          )}
        </div>
      </div>
    </div>
  );
}
