export default function InputEmail() {
  const regex = /^[A-Za-z0-9]{1}[A-Za-z0-9_.-]*@{1}[A-Za-z0-9_.-]+.[A-Za-z0-9]+$/;
  return (
    <input
      className="text-center border"
      type="email"
      placeholder="メールアドレス"
      {...register("email", {
        required: {
          value: true,
          message: '入力してください'
        },
        pattern: {
          value: { regex },
          message: '有効なメールアドレスを入力してください'
        }
      })}
    />
  );
}
