import { useForm } from "react-hook-form";
import { Signup } from '../../apis/signup';

export default function SignupForm() {
  const { register, handleSubmit, formState: { errors } } = useForm();

  const onSubmit = data => {
    data.preventDefault();
    Signup(data.name, data.email, data.password, data.password_confirmation);
  }

  return (
    <div>
      <form onSubmit={handleSubmit(onSubmit)}>
        <h2>アカウントを作成</h2>
        <p>アカウントを作成することにより、利用規約およびプライバシポリシーに同意するものとします。</p>
        <div>
          <input 
            type="text" 
            placeholder="ユーザー名"
            {...register("multipleErrorInput", {
              required: "入力してください",
              pattern: {
                value: /\d+/,
                message: "This input is number only."
              },
              maxLength: {
                value: 20,
                message: "20文字以内で入力してください"
              }
            })} 
          />
          <ErrorMessage
            errors={errors}
            name="multipleErrorInput"
            render={({ messages }) => {
              console.log("messages", messages);
              return messages
                ? Object.entries(messages).map(([type, message]) => (
                    <p key={type}>{message}</p>
                  ))
                : null;
            }}
          />




          <input type="email" placeholder="メールアドレス" {...register("email", { required: true, pattern: /\A[\w+\-.]+@[a-z\d\-]+(\.[a-z\d\-]+)*\.[a-z]+\z/i })} />
          <input type="password" placeholder="パスワード" {...register("password", { required: true, minLength: 6 })} />
          <input type="password" placeholder="パスワード確認" {...register("password_confirmation", { required: true, minLength: 6 })} />
        </div>
        <div>
          <input type="submit" value="新規登録" />
        </div>
      </form>
    </div>
  );
}
