    }
    setLoading(false);
  };

  useEffect(() => {
    handleGetWords();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div>
      <DashboardHeader />
      <div className="flex flex-col items-center top-color h-96">
        <div className='mt-6 text-color'>
          カード名: {card}<br />
          id: {id}
        </div>
        <div className='m-6'>
          <Link to="/learning" state={{card: card, id: id, words: words}} ><LearningButton text="暗記する" /></Link>
        </div>
        <div className='flex flex-col'>
          {words.map((word) =>
          <div key={word.id} className="flex">
            <Link key={word.question} to="#">
              <div className='flex flex-col items-center w-60 p-6 m-2 rounded-md border hover:bg-gray-100 bg-white'>
                <div className='text-sm'>{word.question}</div>
              </div>
            </Link>
            <Link key={word.answer} to="#">
              <div className='flex flex-col items-center w-60 p-6 m-2 rounded-md border hover:bg-gray-100 bg-white'>
                <div className='text-sm'>{word.answer}</div>
                {/* <div className='mt-3 text-sm'>{word.text}</div> */}
              </div>
            </Link>
          </div>
          )}
        </div>
      </div>
    </div>
  );
}
