export default function Button(props) {
  const common_style = "text-white text-xs py-2 px-4 rounded duration-300";
  let button_style = common_style;

  if (props.color==="blue") {
    button_style += " bg-blue-500 hover:bg-blue-700";
  }

  return (
    <button className={button_style}>{props.text}</button>
  )
}

// propsは背景色bg_color、テキストtext
