import { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../../AuthProvider';
import { getCards } from '../../apis/dashboard';

export default function Dashboard() {
  const { setLoading, currentUser } = useContext(AuthContext);
  const initialStates = [{id: 0, title: "", text: ""}]
  const { cards, setCards } = useState([]);
  const addCards = (res) => {
    console.log(cards);
    setCards([...cards, res?.data.cards]);
    console.log(cards);
  }

  const handleGetCards = async () => {
    try {
      const res = await getCards(currentUser);
      if (res?.data.status === 200) {
        console.log(res?.data.cards[0])
      } else {
        console.log('no cards');
      }
    } catch (err) {
      console.log(err);
    }
    setLoading(false);
  };

  useEffect(() => {
    handleGetCards();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [setCards]);

  return (
    <div>
      <h1>Dashboard</h1>
      <p>{currentUser.name}さんのダッシュボード</p>
      {/* {cards.map((card) => { console.log(card.title) })} */}
    </div>
  );
}
