import { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../../AuthProvider';
import { getCards } from '../../apis/dashboard';

export default function Dashboard() {
  const { setLoading, currentUser } = useContext(AuthContext);
  const { cards, setCards } = useState([]);
  // const addCards = (res) => {
  //   const data = res?.data.cards
  //   console.log(data);
  // }

  const handleGetCards = async () => {
    try {
      const res = await getCards(currentUser);
      if (res?.data.status === 200) {
        const data0 = res?.data.cards[0];
        const data1 = res?.data.cards[1];
        setCards(cards.push(data0,data1));
        console.log(cards);
      } else {
        console.log('no cards');
      }
    } catch (err) {
      console.log(err);
    }
    setLoading(false);
  };

  useEffect(() => {
    handleGetCards();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div>
      <h1>Dashboard</h1>
      <p>{currentUser.name}さんのダッシュボード</p>
      <p>id:{currentUser.id}</p>
      <p>email:{currentUser.email}</p>
      <p>password_digest:{currentUser.password_digest}</p>
      <p>created_at:{currentUser.created_at}</p>
      <p>updated_at:{currentUser.updated_at}</p>
      {/* {cards.map((card) => { return <p>{card.title}</p> }) } */}
    </div>
  );
}
