import { useContext } from 'react';
import { AuthContext } from '../../AuthProvider';
import DashboardHeader from "../groups/DashboardHeader";
import Modal from "../modals/Modal";
import SignupForm from "../forms/SignupForm";
import TopSignupButton from "../buttons/TopSignupButton";

export default function Dashboard() {
  const { currentUser, cards } = useContext(AuthContext);

  return (
    <div>
      <DashboardHeader />
      <p>{currentUser.name}</p>
      <ul className='flex items-center'>
        {cards.map((card) =>
          <li key={card.id} className='flex flex-col items-center w-80 p-6 m-4 rounded-md border bg-white'>
            <p>{card.title}</p>
            <p>{card.text}</p>
          </li>
        )}
      </ul>
    </div>
  );
}
