import { useContext, useState, useEffect } from 'react';
import { AuthContext } from '../../AuthProvider';
import { getCards } from '../../apis/dashboard';

export default function Dashboard() {
  const { setLoading, currentUser } = useContext(AuthContext);
  const [cards, setCards] = useState();

  const handleGetCards = async () => {
    try {
      const res = await getCards(currentUser);
      if (res?.data.status === 200) {
        setCards( prevCards => {
          const newCards = res?.data.cards;
          return newCards;
        });
        console.log(res?.data.cards)
        console.log(cards); // 初回レンダー時にcardsに配列が入っていない問題
      } else {
        console.log('no cards');
      }
    } catch (err) {
      console.log(err);
    }
    setLoading(false);
  };

  useEffect(() => {
    handleGetCards();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [setCards]);

  return (
    <div>
      <h1>Dashboard</h1>
      <p>{currentUser.name}さんのダッシュボード</p>
      <p>id:{currentUser.id}</p>
      <p>email:{currentUser.email}</p>
      <p>password_digest:{currentUser.password_digest}</p>
      <p>created_at:{currentUser.created_at}</p>
      <p>updated_at:{currentUser.updated_at}</p>
      {/* {cards[0].title} */}
    </div>
  );
}
