import { useState } from 'react';
import { useLocation } from 'react-router-dom';
import DashboardHeader from "../groups/DashboardHeader";

export const Learning = () => {
  const location = useLocation();
  const { card, id, words } = location.state;
  const [state, setState] = useState("transition-none");
  const handleOnClick =()=>{
    setState("transition-right");
  }

  return (
    <div>
      <DashboardHeader />
      <div className="flex flex-col items-center top-color h-96">
        <div className='mt-6 text-color'>
          カード名: {card}<br />
          id: {id}
        </div>
        <div className='relative w-full'>
          {words.map((word) =>
            <div key={word.id} className={`flex justify-center items-center w-full h-64 border bg-whit absolute ${state}`} onClick={handleOnClick}>
              <div className='flex flex-col items-center border'>
                <div className="">{word.question}</div>
                <div className='mt-3'>{word.answer}</div>
                <div className='mt-3'>{word.text}</div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
