import { useForm } from "react-hook-form";
import { Signup } from '../../apis/signup';
import styles from './SignupForm.module.css';

export default function SignupForm() {
  const { register, handleSubmit, formState: { errors } } = useForm({
    mode: 'onChange',
    criteriaMode: 'all',
  });

  const onSubmit = data => {
    data.preventDefault();
    Signup(data.name, data.email, data.password, data.password_confirmation);
  }

  return (
    <div>
      <form onSubmit={handleSubmit(onSubmit)}>
        <h2>アカウントを作成</h2>
        <p>アカウントを作成することにより、利用規約およびプライバシポリシーに同意するものとします。</p>
        <div>
          <input className={styles.test}
            type="text" 
            placeholder="ユーザー名"
            {...register("name", { 
              required: {
                value: true,
                message: '入力してください'
              },
              maxLength: {
                value: 20,
                message: '20文字以内で入力してください'
              },
            })}
          />
          {errors.name?.types.maxLength && (
            <div>{errors.name.message}</div>
          )}
          <input className={styles.test} type="email" placeholder="メールアドレス" {...register("email", { required: true, pattern: /\A[\w+\-.]+@[a-z\d\-]+(\.[a-z\d\-]+)*\.[a-z]+\z/i })} />
          <input className={styles.test} type="password" placeholder="パスワード" {...register("password", { required: true, minLength: 6 })} />
          <input className={styles.test} type="password" placeholder="パスワード確認" {...register("password_confirmation", { required: true, minLength: 6 })} />
        </div>
        <div>
          <input className={styles.test} type="submit" value="新規登録" />
        </div>
      </form>
    </div>
  );
}
