import { useContext } from 'react';
import { AuthContext } from '../../AuthProvider';
import { useForm } from "react-hook-form";
import patchUser from '../../apis/patchUser';

export default function ProfileForm() {
  const { setLoading, setCurrentUser, currentUser } = useContext(AuthContext);
  const { register, handleSubmit, formState: { errors, isDirty, isValid } } = useForm({
    mode: 'onBlur',
    criteriaMode: 'all',
    defaultValues: {
      name: currentUser.name,
      email: currentUser.email,
    },
  });

  const regex = new RegExp(/\A[\w+\-.]+@[a-z\d\-]+(\.[a-z\d\-]+)*\.[a-z]+\z/i);

  const onSubmit = async (data) => {
    try {
      const res = await patchUser(currentUser.id, data.name, data.email);
      if (res?.data.status === 200) {
        setCurrentUser(res?.data.user);
      } else {
        console.log('no current user');
      }
    } catch (err) {
      console.log(err);
    }
    setLoading(false);
  };

  return (
    <div>
      <form onSubmit={handleSubmit(onSubmit)} className="flex flex-col items-center w-80 p-6 rounded-md bg-white">
        <div className='text-center'>
          <h2 className='text-2xl font-bold'>プロフィール</h2>
        </div>
        <div className='flex flex-col items-center mt-8'>
          <input
            className="text-center border"
            type="text"
            placeholder="ユーザー名"
            autoComplete='username'
            {...register("name", {
              required: {
                value: true,
                message: '入力してください'
              },
              maxLength: {
                value: 20,
                message: '20文字以内で入力してください'
              },
            })}
          />

          {errors.name?.types.required && (
            <div className='text-red-500'>{errors.name.message}</div>
          )}
          {errors.name?.types.maxLength && (
            <div className='text-red-500'>{errors.name.message}</div>
          )}

          <input
            className="text-center border mt-2"
            type="email"
            placeholder="メールアドレス"
            autoComplete='email'
            {...register("email", {
              required: {
                value: true,
                message: '入力してください'
              },
              pattern: {
                value: { regex },
                message: '有効なメールアドレスを入力してください'
              }
            })}
          />

          {errors.email?.types.required && (
            <div className='text-red-500'>{errors.email.message}</div>
          )}
          {errors.email?.types.pattern && (
            <div className='text-red-500'>{errors.email.message}</div>
          )}

        </div>
        <div>
          <input className="button-color
                              button-color:hover
                               text-white
                              py-3 
                              px-12
                              rounded-md
                              duration-300
                              mt-4
                              disabled:bg-gray-200"
            type="submit" value="更新" disabled={!isDirty || !isValid} />
        </div>
      </form>
    </div>
  );
}
