import { useContext } from 'react';
import { AuthContext } from '../../AuthProvider';
import { useForm } from "react-hook-form";
import { Navigate } from 'react-router-dom';
import { Login } from '../../apis/login';
import styles from './SignupForm.module.css';

export default function LoginForm() {
  const { register, handleSubmit, formState: { errors } } = useForm({
    mode: 'onBlur',
    criteriaMode: 'all',
  });
  const { isSignedIn, setIsSignedIn, setCurrentUser } = useContext(AuthContext);
  const regex = /^[A-Za-z0-9]{1}[A-Za-z0-9_.-]*@{1}[A-Za-z0-9_.-]+.[A-Za-z0-9]+$/;

  const onSubmit = (data) => {
    const response = Login(data.email, data.password, data.password_confirmation);
  }

  return (
    <div>
      <form onSubmit={handleSubmit(onSubmit)}>
        <h2>ログイン</h2>
        <div>
          <input
            className={styles.test}
            type="email"
            placeholder="メールアドレス"
            {...register("email", {
              required: {
                value: true,
                message: '入力してください'
              },
              pattern: {
                value: { regex },
                message: '有効なメールアドレスを入力してください'
              }
            })}
          />

          {errors.email?.types.required && (
            <div>{errors.email.message}</div>
          )}
          {errors.email?.types.pattern && (
            <div>{errors.email.message}</div>
          )}

          <input
            className={styles.test}
            type="password"
            placeholder="パスワード"
            {...register("password", {
              required: {
                value: true,
                message: '入力してください'
              },
              minLength: {
                value: 6,
                message: '6文字以上で入力してください'
              }
            })}
          />

          {errors.password?.types.required && (
            <div>{errors.password.message}</div>
          )}
          {errors.password?.types.minLength && (
            <div>{errors.password.message}</div>
          )}

          <input
            className={styles.test}
            type="password"
            placeholder="パスワード確認"
            {...register("password_confirmation", {
              required: {
                value: true,
                message: '入力してください'
              },
              minLength: {
                value: 6,
                message: '6文字以上で入力してください'
              }
            })}
          />

          {errors.password_confirmation?.types.required && (
            <div>{errors.password_confirmation.message}</div>
          )}
          {errors.password_confirmation?.types.minLength && (
            <div>{errors.password_confirmation.message}</div>
          )}

        </div>
        <div>
          <input className={styles.test} type="submit" value="ログイン" />
        </div>
      </form>
    </div>
  );
}
