import { useState } from "react";
import { motion, AnimatePresence } from 'framer-motion'
const array = [{ id: 1, title: "リンク" }, { id: 2, title: "<a>" }, { id: 3, title: "段落" }, { id: 4, title: "<p>" }]

export const Test = () => {
  const [[word_id, direction], setWordId] = useState([0, 1]);
  const handleOnClick = (newDirection) => {
    setWordId([word_id + newDirection, newDirection]);
  };
  const variants = {
    enter: (direction) => { return { x: direction > 0 ? 0 : -1000 } },
    center: { x: 0 },
    exit: (direction) => { return { x: direction > 0 ? -1000 : 0 } }
  };

  console.log(word_id)
  console.log(direction)

  return (
    <AnimatePresence initial={false} custom={direction}>
      <motion.div key={array[word_id].id} variants={variants} initial="enter" custom={direction}
        animate="center" exit="exit" transition={{ type: "tween", duration: 0.5 }} className="relative">
        <div className="absolute right-0 w-1/2 h-screen" onClick={() => handleOnClick(1)}></div>
        <div className="absolute w-1/2 h-screen" onClick={() => handleOnClick(-1)}></div>
        <div className="flex justify-center items-center absolute -z-10 w-full h-screen bg-white border text-6xl">
          {array[word_id].title}
        </div>
      </motion.div>
    </AnimatePresence>
  );
}
