import { useEffect, useContext } from 'react';
import { AuthContext } from '../../AuthProvider';
import { useForm } from "react-hook-form";
import { Navigate } from 'react-router-dom';
import Signup from '../../apis/signup';
import styles from './SignupForm.module.css';
import axios from 'axios';

export default function SignupForm() {
  const { register, handleSubmit, formState: { errors } } = useForm({
    mode: 'onBlur',
    criteriaMode: 'all',
  });

  const { loading, setLoading, isSignedIn, setIsSignedIn, setCurrentUser } = useContext(AuthContext);
  const regex = /^[A-Za-z0-9]{1}[A-Za-z0-9_.-]*@{1}[A-Za-z0-9_.-]+.[A-Za-z0-9]+$/;

  const onSubmit = data => {
    const url = 'http://localhost:3000/signup';
    const user = {
      name: user_data.name,
      email: user_data.email,
      password: user_data.password,
      password_confirmation: user_data.password_confirmation
    }

      const res = axios.post(url, user, { withCredentials: true }
        ).then(function (response) {
          if (res?.data.logged_in === true) {
            setIsSignedIn(true);//ログインuserがいたらtrue
            setCurrentUser(response?.data.user);//ログインuserをセット
          } else {
            console.log('no current user');
          }
        }).catch(function (error) {
          console.log(error);
        });   
  };

  if (isSignedIn===true) { 
    return <Navigate to='/dashboard' /> 
  } else {
  return (
    <div>
      <form onSubmit={handleSubmit(onSubmit)}>
        <h2>アカウントを作成</h2>
        <p>アカウントを作成することにより、利用規約およびプライバシポリシーに同意するものとします。</p>
        <div>
          <input
            className={styles.test}
            type="text"
            placeholder="ユーザー名"
            {...register("name", {
              required: {
                value: true,
                message: '入力してください'
              },
              maxLength: {
                value: 20,
                message: '20文字以内で入力してください'
              },
            })}
          />

          {errors.name?.types.required && (
            <div>{errors.name.message}</div>
          )}
          {errors.name?.types.maxLength && (
            <div>{errors.name.message}</div>
          )}

          <input
            className={styles.test}
            type="email"
            placeholder="メールアドレス"
            {...register("email", {
              required: {
                value: true,
                message: '入力してください'
              },
              pattern: {
                value: { regex },
                message: '有効なメールアドレスを入力してください'
              }
            })}
          />

          {errors.email?.types.required && (
            <div>{errors.email.message}</div>
          )}
          {errors.email?.types.pattern && (
            <div>{errors.email.message}</div>
          )}

          <input
            className={styles.test}
            type="password"
            placeholder="パスワード"
            {...register("password", {
              required: {
                value: true,
                message: '入力してください'
              },
              minLength: {
                value: 6,
                message: '6文字以上で入力してください'
              }
            })}
          />

          {errors.password?.types.required && (
            <div>{errors.password.message}</div>
          )}
          {errors.password?.types.minLength && (
            <div>{errors.password.message}</div>
          )}

          <input
            className={styles.test}
            type="password"
            placeholder="パスワード確認"
            {...register("password_confirmation", {
              required: {
                value: true,
                message: '入力してください'
              },
              minLength: {
                value: 6,
                message: '6文字以上で入力してください'
              }
            })}
          />

          {errors.password_confirmation?.types.required && (
            <div>{errors.password_confirmation.message}</div>
          )}
          {errors.password_confirmation?.types.minLength && (
            <div>{errors.password_confirmation.message}</div>
          )}

        </div>
        <div>
          <input className={styles.test} type="submit" value="新規登録" />
        </div>
      </form>
    </div>
  );
  }
}
