import { useEffect, useContext, useReducer } from 'react';
import { useParams } from 'react-router-dom';
import { AuthContext } from '../../AuthProvider';
import DashboardHeader from "../groups/DashboardHeader";
import { Word } from '../groups/Word';
import { initialState, wordsActionTypes, wordsReducer } from '../../reducers/words'
import { fetchWords } from '../../apis/words';
import { new_words } from '../../helper';
new_words

export const Learning = () => {
  const { cards } = useContext(AuthContext);
  const [state, dispatch] = useReducer(wordsReducer, initialState)
  const { id } = useParams();
  const card = cards[id - 1].title;
  // const handleOnClick = () => {
  //   setWordId(word_id + 1);
  // }

  useEffect(() => {
    dispatch({ type: wordsActionTypes.FETCHING });
    fetchWords(id)
      .then((data) => {
        const question_words = data.words.map(question_word => { return { title: question_word.question } });
        const answer_words = data.words.map(answer_word => { return { title: answer_word.answer, text: answer_word.text } });
        const length = question_words.length * 2 - 1;
        const new_words = [];
        for (let i = 0; i <= length; i++) {
          if (i % 2 === 0) {
            new_words.push({ id: i, title: question_words[i / 2].title, text: '' });
          } else {
            new_words.push({ id: i, title: answer_words[(i - 1) / 2].title, text: answer_words[(i - 1) / 2].text });
          }
        }
        dispatch({
          type: wordsActionTypes.FETCH_SUCCESS,
          payload: {
            words: new_words
          }
        })
      })
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, []);

  return (
    <div>
      <DashboardHeader />
      <div className="flex flex-col items-center top-color h-96">
        <div className='mt-6 text-color'>
          カード名: {card}<br />
          カードid: {id}
        </div>
        <div className='relative w-full'>
          <Word word={state.words[0]} />
        </div>
      </div>
    </div>
  );
}
