import { useState } from 'react';
import { useLocation } from 'react-router-dom';
import DashboardHeader from "../groups/DashboardHeader";
import { Word } from '../groups/Word';

export const Learning = () => {
  const location = useLocation();
  const { card, id, words } = location.state;
  const [count, setCount] = useState(1);

  const handleOnClick =()=>{
    if (props.count % 2 === 0) {
      word_id = (props.count - (props.count / 2));
      setState(props.words[word_id].answer);
    } else {
      word_id = (props.count - ((props.count - 1) / 2));
      setState(props.words[word_id].question);
    }
    setCount(count+1);
  }

  return (
    <div>
      <DashboardHeader />
      <div className="flex flex-col items-center top-color h-96">
        <div className='mt-6 text-color'>
          カード名: {card}<br />
          カードid: {id}
        </div>
        <div className='relative w-full'>
          <Word words={words} count={count} onClick={handleOnClick}/>
        </div>
      </div>
    </div>
  );
}
