    setState(props.words[props.count].answer);
  } else{

    setState(props.words[props.count].question);
  }

  return (
    <div>
      <div className="flex flex-col justify-center h-64 border bg-white" onClick={props.onClick}>
        <div className='flex flex-col items-center bg-white'>
          <div className=''>{state}</div>
          {/* <div className='mt-3 text-sm'>{props.word.answer}</div>
          <div className='mt-3 text-sm'>{props.word.text}</div> */}
        </div>
      </div>
    </div>
  );
}
