import { useState } from "react";
import { motion, AnimatePresence, useAnimationControls } from 'framer-motion'
const array = [{id:1,title:"リンク"},{id:2,title:"<a>"},{id:3,title:"段落"},{id:4,title:"<p>"}]

export const Test =()=>{
  const [count, setCount] = useState(0)
  const controls = useAnimationControls();
  const handleClickRight =()=>{
    setCount(count+1);
    controls.start({})
  }
  const handleClickLeft =()=>{
    setCount(count-1);
  }

  return(
    <AnimatePresence>
      <motion.div key={array[count].id} exit={controls} transition={{type: "tween", duration: 0.5}}>
        <div className="h-96 w-48 absolute left-48" onClick={handleClickRight}></div>
        <div className="h-96 w-48 absolute" onClick={handleClickLeft}></div>
        <div className="flex justify-center items-center text-3xl w-96 bg-red-500 h-96 border absolute -z-10">
          {array[count].title}
        </div>
      </motion.div>
      </AnimatePresence>
  );
}
